package com.java8;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;

public class SubstringWithConcatenation {
	public static void main(String[] args) {
		String s = "";
		String[] words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
		
		findSubstring(s, words);
	}
	
	public static List<Integer> findSubstring(String s, String[] words) {
		int wordLength = 0;
		int wordsCnt = 0;
		int subStringLength = 0;

		if (s == null || words == null || words.length == 0) {
			return new ArrayList<Integer>();
		}

		wordLength = words[0].length();
		wordsCnt = words.length;
		subStringLength = wordLength * wordsCnt;

		if (s.length() < subStringLength) {
			return new ArrayList<Integer>();
		}

		if (wordLength == 0) {
			return new ArrayList<Integer>();
		}

		int start = 0;
		List<Integer> indexList = new ArrayList<Integer>();

		Arrays.sort(words);
		List<String> wordsList = Arrays.asList(words);
		
		Set<Character> initialCh = new HashSet<Character>();
		
		for(String word : words){
			initialCh.add(word.charAt(0));
		}

		while (start < s.length() - subStringLength) {
			int matchCnt = 0;
			int tmpStart = start;
			int tmpEnd = tmpStart + wordLength;
			Map<String, Integer> wordSearchIndex = new HashMap<String, Integer>();
			
			if(!initialCh.contains(s.charAt(start))) {
				start++;
				continue;
			}

			while (tmpStart < s.length() && tmpEnd <= s.length()
					&& tmpEnd <= (start + subStringLength)) {
				String sub = s.substring(tmpStart, tmpEnd);
				int index = 0;
				boolean hasStoredIndex = false;

				if (wordSearchIndex.containsKey(sub)) {
					index = wordSearchIndex.get(sub);
					hasStoredIndex = true;
				}

				if (hasStoredIndex) {
					if (index + 1 < wordsCnt) {
						if (words[index + 1].equals(sub)) {
							wordSearchIndex.put(sub, index + 1);
							tmpStart = tmpEnd;
							tmpEnd = tmpStart + wordLength;
							matchCnt++;
							continue;
						} else {
							break;
						}
					} else {
						break;
					}
				} else {
					index = wordsList.indexOf(sub);

					if (index >= 0) {
						wordSearchIndex.put(sub, index);
						tmpStart = tmpEnd;
						tmpEnd = tmpStart + wordLength;
						matchCnt++;
						continue;
					} else {
						break;
					}
				}
			}

			if (matchCnt == wordsCnt) {
				indexList.add(start);
			}

			start++;
		}

		return indexList;
	}
}
